\begin{align*}
\mathcal{L}^S &amp;= \mathcal{L}_{ce}(\hat{y}_{LR}^{S}, y_{LR}^{S}, 1) \\
\mathcal{L}_{ce}(\hat{y}, y, q) &amp;= -\sum_{i=1}^{H(y)} \sum_{j=1}^{W(y)} \sum_{c=1}^{C} q_{ij}y_{ijc} \log{\zeta(\hat{y}, \frac{H(y)}{H(\hat{y})})_{ijc}}
\end{align*}
